import React from 'react'
import { useContext } from 'react'
import { ContextGlobal } from "../Components/utils/ContextGlobalProvider";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {
  const { contextValue } = useContext(ContextGlobal);
  const { toggleTheme } = contextValue;
  const { theme } = contextValue;

  const handleClick = () => {
    toggleTheme();
  };

  return (
    <header className="sticky-top">
      <nav
        className={`navbar navbar-expand-sm container ${
          theme === 'dark' ? 'navbar-dark bg-dark' : 'navbar-light bg-light'
        }`}
        aria-label="Third navbar example"
      >
        <div className="container">
          <a className={`navbar-brand`} href="/home">
            DH Odonto
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarsExample03"
            aria-controls="navbarsExample03"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div
            className="collapse navbar-collapse justify-content-end"
            id="navbarsExample03"
          >
            <ul className={`navbar-nav mb-2 mb-sm-0`}>
              <li className={`nav-item`}>
                <a className="nav-link" href="/home">
                  Home
                </a>
              </li>
              <li className={`nav-item `}>
                <a className="nav-link" href="/contact">
                  Contact
                </a>
              </li>
              <li className={`nav-item`}>
                <a className="nav-link" href="/favs">
                  Favorites
                </a>
              </li>
              <li className={`nav-item`}>
                <button
                  className={`btn ${theme === 'dark' ? 'btn-dark' : 'btn-light'}`}
                  onClick={() => handleClick()}
                >
                  â˜€ ðŸŒ™
                </button>
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Navbar;
